def p_fuency(sentence):
    if not sentence:
        return {}
    known_ps = ['hello', 'world', 'spot', 'go']
    ps = []
    i = 0
    while i < len(sentence):
        for p in known_ps:
            if sentence.startswith(p, i):
                ps.append(p)
                i += len(p)
                break
        else:
            i += 1
    f = {}
    for p in ps:
        f[p] = f.get(p, 0) + 1
    return f

